/**
 * Left trim slash
 * 
 * @api public
 */

exports.ltrimSlash = function(string) {
  if(string[0] === '/') {
    return string.substr(1);
  } else {
    return string;
  }
}

/**
 * Right trim slash
 * 
 * @api public
 */

exports.rtrimSlash = function(string) {
  if(string[(string.length - 1)] === '/') {
    return string.substr(0, (string.length - 1));
  } else {
    return string;
  }
}

/**
 * Adds final slash if not present
 * 
 * @api public
 */

exports.addSlash = function(string) {
  if(string[(string.length - 1)] !== '/') {
    return string + '/';
  } else {
    return string;
  }
}

/**
 * Get current date and time formatted as _month_/_day_ _hour_:_minute_
 * 
 * @api public
 */

exports.getFormattedDateTime = function() {
  var d = new Date();
  return d.toDateString() + ' ' + d.toTimeString();
}